import 'package:http/http.dart' as http;
import 'package:http/testing.dart';
import 'package:marvel_heroes/app/modules/marvel_characters/service/marvel_service.dart';
import 'package:mocktail/mocktail.dart';
import 'package:flutter_test/flutter_test.dart';

class ClientMock extends Mock implements http.Client {}

class MarvelServiceMock extends Mock implements MarvelService {}

class FakeUri extends Fake implements Uri {}

void main() {
  final client = ClientMock();
  final repository = MarvelServiceMock();

  setUpAll(() {
    registerFallbackValue(FakeUri());
  });

  test('Should return a character model', () async {
    when(() => client.get(any())).thenAnswer((_) async => http.Response(apiJsonResponseMock, 200));
    final response = await repository.getAllCharacters(0);

    expect(response, equals(true));
  });
}

